import * as React from 'react';
import Head from 'next/head';
import BlogPosts from '../components/Blog/BlogPosts/BlogPosts';
import Layout from '../components/Layout/Layout';
import { pageProps } from './_app';

export interface IndexProps extends pageProps {
  posts: any;
  categories: any;
  postsForFooter: any;
}

export interface IndexState {}

class Index extends React.Component<IndexProps, IndexState> {
  constructor(props: IndexProps) {
    super(props);
    this.state = {};
  }
  render() {
    return (
      <div dir="rtl" id="blog">
        <Head>
          <title>جی بلاگ | آموزش ها و مقالات کاربردی برای وبمستران</title>
          <meta name="description" content="Generated by create next app" />
          <link rel="icon" href="/favicon.ico" />
        </Head>

        <Layout
          postsForFooter={this.props.postsForFooter}
          domainsForNavbar={this.props.domainsForNavbar}
        >
          <BlogPosts
            posts={this.props.posts}
            categories={this.props.categories}
          />
        </Layout>
      </div>
    );
  }
}

export async function getServerSideProps(context) {
  const locale = context.locale;

  if (locale !== 'fa') {
    return {
      notFound: true,
    };
  }

  const postsForFooterRes = await fetch(
    'https://jsonblob.com/api/jsonBlob/ff048401-e7cd-11eb-971c-9ff88820de62'
  );
  const postsForFooter = await postsForFooterRes.json();

  const postsRes = await fetch(
    'https://jsonblob.com/api/jsonBlob/d8eccd84-d821-11eb-9f33-07821a14b37b'
  );
  const postsData = await postsRes.json();

  const categoriesRes = await fetch(
    'https://jsonblob.com/api/jsonBlob/7bc3650b-d8c2-11eb-8f97-fba7e85c29a8'
  );
  const categoriesData = await categoriesRes.json();

  return {
    props: {
      posts: postsData,
      categories: categoriesData.categories,
      postsForFooter,
    },
  };
}

export default Index;
