import * as React from 'react';
import Head from 'next/head';
import Footer from '../../components/Footer/Footer';
import Navbar from '../../components/Navbar/Navbar';
import BlogPost from '../../components/Blog/BlogPost/BlogPost';

export interface IndexProps {
  post: any;
  categories: any;
  postTitle: string;
}

export interface IndexState {}

class Index extends React.Component<IndexProps, IndexState> {
  constructor(props: IndexProps) {
    super(props);
    this.state = {};
  }
  render() {
    return (
      <div dir="rtl" id="blog-post">
        <Head>
          <title>جی بلاگ | {this.props.postTitle}</title>
          <meta name="description" content="Generated by create next app" />
          <link rel="icon" href="/favicon.ico" />
        </Head>

        <Navbar />

        <BlogPost categories={this.props.categories} post={this.props.post} />

        <Footer />
      </div>
    );
  }
}

export async function getServerSideProps(context) {
  const postTitle = context.query.post;

  const categoriesRes = await fetch(
    'https://jsonblob.com/api/jsonBlob/7bc3650b-d8c2-11eb-8f97-fba7e85c29a8'
  );
  const categoriesData = await categoriesRes.json();

  const postRes = await fetch(
    'https://jsonblob.com/api/jsonBlob/2e53bd45-d8fc-11eb-8f97-17844c58c504'
  );
  const post = await postRes.json();

  return {
    props: {
      post,
      postTitle,
      categories: categoriesData.categories,
    },
  };
}

export default Index;
