import * as React from 'react';
import Head from 'next/head';
import Footer from '../../../components/Footer/Footer';
import Navbar from '../../../components/Navbar/Navbar';
import SharedHosting from '../../../components/HostsPricing/SharedHosting/SharedHosting';

export interface IndexProps {
  sharedHosts: any;
}

export interface IndexState {}

class Index extends React.Component<IndexProps, IndexState> {
  constructor(props: IndexProps) {
    super(props);
    this.state = {};
  }
  render() {
    return (
      <div dir="rtl">
        <Head>
          <title>شماره حساب ها</title>
          <meta name="description" content="Generated by create next app" />
          <link rel="icon" href="/favicon.ico" />
        </Head>

        <Navbar />

        <SharedHosting sharedHosts={this.props.sharedHosts} />

        <Footer />
      </div>
    );
  }
}

export default Index;

export async function getServerSideProps(context) {
  const sharedHostsRes = await fetch(
    `https://jsonblob.com/api/jsonBlob/874ffbe1-dfe6-11eb-a8ab-7d0108d78d09`
  );
  const sharedHosts = await sharedHostsRes.json();

  return {
    props: {
      sharedHosts,
    },
  };
}
