import * as React from 'react';
import Head from 'next/head';
import Layout from '../../components/Layout/Layout';
import { IPageProps } from '../_app';
import License from '../../components/License/License';
import { renderPageTitle } from '../../components/License/helper/renderPageTitle';
import ILicense from '../../helper/types/products/License/plan';

interface IProps extends IPageProps {
  plans: ILicense[];
  license: 'cloudlinux' | 'cpanel' | 'directadmin' | 'litespeed' | 'whmcs';
}

class Index extends React.Component<IProps> {
  render() {
    return (
      <div dir="rtl">
        <Head>
          <title>لایسنس | {renderPageTitle(this.props.license)}</title>
          <meta name="description" content="Generated by create next app" />
          <link rel="icon" href="/favicon.ico" />
        </Head>

        <Layout header={this.props.header} footer={this.props.footer}>
          <License license={this.props.license} plans={this.props.plans} />
        </Layout>
      </div>
    );
  }
}

export async function getServerSideProps(context) {
  const locale = context.locale;
  const license = context.query.license;

  if (locale !== 'fa') {
    return {
      notFound: true,
    };
  }

  const respone = await fetch(
    `${process.env.SITE_URL}/${locale}/licenses/${license}?ajax=1`
  );
  const data = await respone.json();

  if (!data.status) {
    return {
      notFound: true,
    };
  }

  return {
    props: { ...data, license },
  };
}

export default Index;
